import React, { useState, useEffect } from 'react';

function ViewProjects() {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Ensure the URL is correct
    fetch('http://127.0.0.1:5000/api/projects')  // Use full API URL
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setProjects(data))
      .catch((error) => console.error('Error fetching projects:', error));
  }, []);

  return (
    <div className="view-projects-page">
      <h2>All Capstone Projects</h2>
      <ul>
        {projects.map((project) => (
          <li key={project.id}>
            <h3>{project.title}</h3>
            <p><strong>Abstract:</strong> {project.abstract}</p>
            <p><strong>Team Members:</strong> {project.team_members}</p>
            <p><strong>Created At:</strong> {new Date(project.created_at).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ViewProjects;
