import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function UserProfile() {
  const { userId } = useParams();  // Extract the userId from the URL params
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the user details based on the userId
    const fetchUserDetails = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/user/${userId}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user details');
        }

        const data = await response.json();
        setUser(data);
      } catch (error) {
        setError('Failed to load user details. Please try again later.');
      }
    };

    fetchUserDetails();
  }, [userId]);

  if (error) {
    return <p className="text-red-500">{error}</p>;
  }

  if (!user) {
    return <p>Loading...</p>;
  }

  return (
    <div className="user-profile max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-md mt-12">
      <h2 className="text-2xl font-bold mb-6">User Profile</h2>
      <div className="user-details">
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
        <p><strong>Department:</strong> {user.department || 'N/A'}</p>
        <h3 className="text-xl font-semibold mt-6 mb-4">Projects</h3>
        <ul className="list-disc pl-6">
          {user.projects.map((project) => (
            <li key={project.id}>{project.title}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default UserProfile;
