import React, { useState, useEffect } from 'react';
import UploadProject from './UploadProject'; // Reuse the UploadProject component

function AdminPage() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  // Fetch all users from the API
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/users', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }

        const data = await response.json();
        setUsers(data);
      } catch (error) {
        setError('Failed to load users. Please try again later.');
      }
    };

    fetchUsers();
  }, []);

  return (
    <div className="admin-page max-w-6xl mx-auto p-6 bg-white rounded-xl shadow-md mt-12">
      <h2 className="text-3xl font-bold mb-6">Admin Dashboard</h2>

      {/* Display Users */}
      <div className="mb-8">
        <h3 className="text-2xl font-semibold mb-4">All Users</h3>
        {error && <p className="text-red-500">{error}</p>}
        <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow">
          <thead>
            <tr>
              <th className="px-4 py-2 border-b">ID</th>
              <th className="px-4 py-2 border-b">Name</th>
              <th className="px-4 py-2 border-b">Email</th>
              <th className="px-4 py-2 border-b">Role</th>
              <th className="px-4 py-2 border-b">Department</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user.id}>
                  <td className="px-4 py-2 border-b">{user.id}</td>
                  <td className="px-4 py-2 border-b">{user.name}</td>
                  <td className="px-4 py-2 border-b">{user.email}</td>
                  <td className="px-4 py-2 border-b">{user.role}</td>
                  <td className="px-4 py-2 border-b">{user.department || 'N/A'}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="px-4 py-2 text-center">
                  No users found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Upload Project Section */}
      <div className="upload-project-section">
        <h3 className="text-2xl font-semibold mb-4">Upload Project</h3>
        <UploadProject /> {/* Reuse the UploadProject component */}
      </div>
    </div>
  );
}

export default AdminPage;
