import React, { useState, useEffect } from 'react';

function ViewProjects() {
  const [projects, setProjects] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState(''); // State to store selected department
  const [sortedProjects, setSortedProjects] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch projects from the backend
    const fetchProjects = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/projects', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch projects');
        }

        const data = await response.json();
        setProjects(data);
        setSortedProjects(data); // Set to all projects initially
      } catch (error) {
        setError('Failed to load projects. Please try again later.');
      }
    };

    // Fetch departments from the backend
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/departments', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch departments');
        }

        const data = await response.json();
        setDepartments(data); // Set departments from API response
      } catch (error) {
        setError('Failed to load departments. Please try again later.');
      }
    };

    fetchProjects();
    fetchDepartments();
  }, []);

  // Handle department sorting
  const handleSortByDept = (e) => {
    const dept = e.target.value;
    setSelectedDept(dept);

    if (dept === '') {
      // If no department is selected, reset to all projects
      setSortedProjects(projects);
    } else {
      // Filter projects by selected department
      const filteredProjects = projects.filter(
        (project) => project.department === dept
      );
      setSortedProjects(filteredProjects);
    }
  };

  return (
    <div className="view-projects-page max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-md mt-12">
      <h2 className="text-2xl font-bold mb-6">All Capstone Projects</h2>
      {error && <p className="text-red-500">{error}</p>}

      {/* Sorting Dropdown */}
      <div className="mb-4">
        <label htmlFor="sortByDept" className="block mb-2 text-sm font-medium">
          Sort by Department:
        </label>
        <select
          id="sortByDept"
          className="w-full p-2 border border-gray-300 rounded-lg"
          value={selectedDept}
          onChange={handleSortByDept}
        >
          <option value="">All Departments</option>
          {departments.map((dept) => (
            <option key={dept.id} value={dept.name}>
              {dept.name}
            </option>
          ))}
        </select>
      </div>

      {/* Projects List */}
      <ul className="space-y-6">
        {sortedProjects.map((project) => (
          <li key={project.id} className="bg-gray-50 p-6 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
            <p><strong>Department:</strong> {project.department || 'N/A'}</p>
            <p><strong>Created By:</strong> {project.created_by_user?.name || 'Unknown'}</p>
            <p><strong>Team Members:</strong> {project.team_members ? project.team_members.join(', ') : 'N/A'}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ViewProjects;
