import React, { useState } from 'react';

function UploadProject() {
  const [title, setTitle] = useState('');
  const [abstract, setAbstract] = useState('');
  const [teamMembers, setTeamMembers] = useState('');
  const [file, setFile] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('abstract', abstract);
    formData.append('team_members', teamMembers);
    if (file) formData.append('file', file);

    const response = await fetch('/api/upload_project', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      alert('Project uploaded successfully!');
    } else {
      alert('Error uploading project');
    }
  };

  return (
    <div className="upload-project-page max-w-3xl mx-auto p-6 bg-white rounded-xl shadow-md mt-12">
      <h2 className="text-2xl font-bold mb-6">Upload Your Project</h2>
      <form onSubmit={handleUpload}>
        <div className="mb-4">
          <label className="block mb-2">Title:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="w-full px-4 py-2 border rounded-lg"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Abstract:</label>
          <textarea
            value={abstract}
            onChange={(e) => setAbstract(e.target.value)}
            required
            className="w-full px-4 py-2 border rounded-lg"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Team Members:</label>
          <input
            type="text"
            value={teamMembers}
            onChange={(e) => setTeamMembers(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Project File:</label>
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            className="w-full px-4 py-2 border rounded-lg"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
        >
          Upload Project
        </button>
      </form>
    </div>
  );
}

export default UploadProject;
