import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Header({ isAuthenticated, setIsAuthenticated }) {
  const navigate = useNavigate();

  const handleLogout = async () => {
    const response = await fetch('/api/logout', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (response.ok) {
      setIsAuthenticated(false);
      navigate('/login');
    }
  };

  return (
    <header className="bg-gray-800 text-white p-6">
      <div className="header-container max-w-4xl mx-auto flex justify-between items-center">
        <div className="logo text-2xl font-bold">
          <Link to="/home" className="hover:text-gray-300">Capstone Portfolio</Link>
        </div>
        <nav>
          <ul className="flex space-x-6">
            <li><Link to="/home" className="hover:text-gray-300">Home</Link></li>
            <li><Link to="/view-projects" className="hover:text-gray-300">View Projects</Link></li>
            {isAuthenticated ? (
              <>
                <li><Link to="/upload-project" className="hover:text-gray-300">Upload Project</Link></li>
                <li>
                  <button onClick={handleLogout} className="hover:text-gray-300">Logout</button>
                </li>
              </>
            ) : (
              <li><Link to="/login" className="hover:text-gray-300">Login</Link></li>
            )}
          </ul>
        </nav>
      </div>
    </header>
  );
}

export default Header;
