import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Home from './pages/Home';
import UploadProject from './pages/UploadProject';
import ViewProjects from './pages/ViewProjects';
import Header from './components/Header';
import Footer from './components/Footer';
import './styles.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    // Check authentication status on app load
    const checkAuthStatus = async () => {
      try {
        const response = await fetch('/api/auth_status', { credentials: 'include' });
        if (response.ok) {
          const data = await response.json();
          setIsAuthenticated(data.authenticated);
        } else {
          setIsAuthenticated(false);
        }
      } catch (error) {
        console.error("Failed to check authentication status:", error);
        setIsAuthenticated(false);
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };
    checkAuthStatus();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Show a loading spinner while fetching auth status
  }

  return (
    <Router>
      <Header isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
      <div className="App">
        <Routes>
          <Route path="/home" element={<Home />} />
          <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />

          {/* Protected routes */}
          {isAuthenticated ? (
            <>
              <Route path="/upload-project" element={<UploadProject />} />
              <Route path="/view-projects" element={<ViewProjects />} />
              <Route path="/" element={<Navigate to="/home" />} />
            </>
          ) : (
            <>
              <Route path="/upload-project" element={<Navigate to="/login" />} />
              <Route path="/view-projects" element={<Navigate to="/login" />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </>
          )}
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}

export default App;
